exports['NODE_MODULE_PATHS'] = function(test, RequireResolver){

	function collect(path){
		var resolver = new RequireResolver();

		resolver.Path.sep = '\\';
		path = resolver.Path.dirname(path);

		return resolver.nodeModulePaths(path);
	}

	var expected = [
		'C:\\Users\\Damien\\Documents\\GitHub\\nodesite\\node_modules\\proto\\node_modules',
		'C:\\Users\\Damien\\Documents\\GitHub\\nodesite\\node_modules',
		'C:\\Users\\Damien\\Documents\\GitHub\\node_modules',
		'C:\\Users\\Damien\\Documents\\node_modules',
		'C:\\Users\\Damien\\node_modules',
		'C:\\Users\\node_modules',
		'C:\\node_modules'
	];
	var path = 'C:\\Users\\Damien\\Documents\\GitHub\\nodesite\\node_modules\\proto\\index.js';
	var collected = collect(path);

	test.equal(String(expected), String(collected));
};