var Emitter = require('emitter');
var ElementEventEmitter = Emitter.create({
	element: null,
	
	init: function(element, bind){
		this.element = element;
		Emitter.init.call(this, bind || element);
	},

	handleEvent: function(e){
		this.emit(e.type, e);
	}
});

ElementEventEmitter.on('addListener', function(name){
	if( !this.isInternal(name) ){
		this.element.addEventListener(name, this);
	}
});
ElementEventEmitter.on('removeListener', function(name){
	if( !this.isInternal(name) ){
		this.element.removeEventListener(name, this);
	}
});

module.exports = ElementEventEmitter;