exports['bind textNode by calling textNode.bind'] = function(test){
	var textNode = {};
	var model = {
		text: 'coucou'
	};

	test.imports.bind.call(textNode, 'textContent', model, 'text');

	test.equal(textNode.textContent, 'coucou');
	model.text = 'hello';
	test.equal(textNode.textContent, 'hello');
	test.done();
};