exports['bind textContent property to object text property'] = function(test){
	var textNode = {};
	var model = {
		text: 'coucou'
	};

	test.imports.new(textNode, 'textContent', model, 'text');

	test.equal(textNode.textContent, 'coucou');
	model.text = 'hello';
	test.equal(textNode.textContent, 'hello');
	test.done();
};