exports['bind textContent property to object text property'] = function(test, NodeBinding){
	var textNode = {};
	var model = {
		text: 'coucou'
	};

	NodeBinding.create(textNode, 'textContent', model, 'text');

	test.equal(textNode.textContent, 'coucou');
	model.text = 'hello';
	test.equal(textNode.textContent, 'hello');
};