exports['bind textNode by calling textNode.bind'] = function(test){
	var textNode = {};
	var model = {
		text: 'coucou'
	};

	imports.bind.call(textNode, 'textContent', model, 'text');

	test.equal(textNode.textContent, 'coucou');
	model.text = 'hello';
	test.equal(textNode.textContent, 'hello');
	test.done();
};

exports['bind Element attribute by calling Element.bind'] = function(test){
	var element = {
		attributes: {},
		setAttribute: function(name, value){
			this.attributes[name] = value;
		},

		getAttribute: function(name, value){
			return this.attributes[name];
		}
	};
	var model = {
		text: 'coucou'
	};

	imports.bind.call(element, 'name', model, 'text');

	test.equal(element.getAttribute('name'), 'coucou');
	test.done();
};