var Template = {
	TemplateCompiler: require('./TemplateCompiler'),
	TemplateInstance: require('./TemplateInstance'),
	element: null,
	content: null,
	linkers: null,
	toString: function(){ return 'Template'; },

	constructor: function(element){
		if( element.template ) return element.template;
		element.template = this;
		this.element = element;		
		this.content = element.getReference().content;
	},

	createInstance: function(){
		return new this.TemplateInstance(this);
	},

	cloneContent: function(){
		return this.content.cloneNode(true);
	},

	parse: function(){
		return this.TemplateCompiler.parse(this.content, true);
	},

	getLinkers: function(){
		if( this.linkers === null ){
			this.linkers = this.parse();
		}
		return this.linkers;
	}
};

module.exports = Template;