var Template = {
	Instance: require('./templateInstance'),
	Iterator: require('./templateIterator'),
	Parser: require('../parser'),
	element: null,
	content: null,
	templateIterator: null,
	linkers: null,
	toString: function(){ return 'Template'; },

	create: function(element){
		window.HTMLTemplateElement.decorate(element);
		this.element = element;
		element.template = this;
		this.content = element.getReference().content;
	},

	cloneContent: function(){
		return this.content.cloneNode(true);
	},

	createInstance: function(model){
		var instance = this.Instance.new(this);
		instance.setModel(model);
		return instance;
	},

	setModel: function(model){
		this.unsetModel();
		this.model = model;
		this.templateIterator = this.Iterator.new(this);
		this.templateIterator.checkAttributes();
	},

	unsetModel: function(){
		if( this.model ){
			if( this.templateIterator ){
				this.templateIterator.close();
				this.templateIterator = null;
			}
			this.model = null;
		}
	},

	parse: function(){
		return this.Parser.parse(this.content, true);
	},

	getLinkers: function(){
		if( this.linkers === null ){
			this.linkers = this.parse();
		}
		return this.linkers;
	}
};

module.exports = Template;