var os;

if( typeof navigator === 'undefined' ){
	os = {};
}
else{	
	var ua = navigator.userAgent.toLowerCase();
	var hostname = navigator.platform.toLowerCase();
	var platform;

	if( ua.match(/ip(?:ad|od|hone)/) ){
		platform  = 'ios';
	}
	else{
		platform = ua.match(/(?:webos|android)/) || ua.match(/mac|win|linux/);

		if( platform ){
			platform = platform[0];
		}
		else{
			platform = 'other';
		}
	}
		
	os = {
		hostname: hostname,
		platform: platform
	};
}

module.exports = os;


