/*

kinda jasmine test

*/

window.it = function(description, test){	

	try{
		window.expect = function(a){
			return {
				toBe: function(b){
					if( a !== b ){
						throw new Error('expect ' + b + ' and got ' + a);
					}
				}
			};
		};

		test();
	}
	catch(e){
		console.error('TEST FAILED:', description, e);
	}
};