var groupBy = function(array, property){
	var i = 0, j = array.length, group, item, group, groups = {};		

	for(;i<j;i++){
		item = array[i];
		group = item[property];
		delete item[property];

		if( group in groups ){
			groups[group].push(item);
		}
		else{
			groups[group] = [item];
		}
	}

	return groups;
};

module.exports = groupBy;

