var AffectationChange = require('./affectationChange');

module.exports = function(){
	this.applyMethod('reverse', arguments);

	var i = 0, j = this.array.length, index, affectations = [];

	for(;i<j;i++){
		index = j - i - 1;
		if( i != index ){
			affectations.push(index, i);
		}
	}

	this.notify(AffectationChange.new(affectations));

	return this.array;
};