module.exports = function(){
	var length = this.applyMethod('unshift', arguments), i = 0, j = arguments.length, index;

	for(;i<j;i++){
		index = i + length - j;
		// observe value at the end
		this.observeValueAt(index);
		// notify the add value at the beginning
		this.notify({
			type: 'add',
			index: i,
			value: this.array[i]
		});
	}

	return length;
};