/*
*/

require('Iterator');

var proto = require('proto');
var Collector = proto.create({
	Iterator: null,
	iterable: null,
	iterator: null,
	filter: null,
	filterBind: null,	
	generateIndex: false,
	result: {done: false, value: undefined},
	index: 0,

	init: function(iterable, filter, filterBind){
		this.iterable = iterable;
		this.filter = filter;
		this.filterBind = filterBind;
		this.Iterator = proto.createFrom(Iterator, this);
	},

	reset: function(){
		this.iterator = this.iterable['@@iterator']();
		this.index = 0;
		this.result = {done: false, value: undefined};
	},

	next: function(){
		var result, filter, filterBind, iterator, next, value;

		result = this.result;
		filter = this.options.filter;
		filterBind = this.options.filterBind;

		iterator = this.iterator;
		next = iterator.next();
		while( next.done === false ){
			value = next.value;

			if( filter === null || typeof filterBind == 'undefined' ? filter(value) : filter.call(filterBind, value) ){
				if( this.generateIndex === true ){
					value =  typeof iterator.index == 'number' ? iterator.index : this.index;
				}

				result.done = false;
				result.value = value;				

				return result;
			}

			next = iterator.next();
			this.index++;
		}

		result.done = true;
		result.value = undefined;
		return result;
	},

	'@@iterator': function(){
		var iterator = this.Iterator.create();
		iterator.reset();
		return iterator;
	},

	collectIndexes: function(){
		var iterator = this['@@iterator']();
		iterator.generateIndex = true;
		return iterator;
	},

	count: function(){
		return Array.from(this).length;
	}
});

module.exports = Collector;