it('should wait for resolve to call the compute function', function(test){
	var arg = null;
	var computed = module.new(function(values){
		arg = values;
	});

	computed.observe('modelName', {}, 'name', true);

	test.equal(arg, null);
	computed.resolve();
	test.equal(typeof arg, 'object');
	test.done();
});

it('call compute function with an object with values named', function(test){

	var arg = null;
	var computed = module.new(function(values){
		arg = values;
	});
	var model = {
		name: 'damien',
		age: 10
	};

	computed.observe('modelName', model, 'name');
	computed.observe('modelAge', model, 'age');

	computed.resolve();
	test.equal(arg.modelName, 'damien');
	test.equal(arg.modelAge, 10);
	test.done();
});