/*
*/
var FilteredIterator = require('./FilteredIterator');
var proto = require('proto');
var FilteredIterable = proto.create({
	iterable: null,
	filter: null,
	bind: null,

	constructor: function(iterable, filter, bind){
		this.iterable = iterable;
		this.filter = filter;
		this.bind = bind;
	},

	'@@iterator': function(){
		return new FilteredIterator(this.iterable['@@iterator'](), this.filter, this.bind);
	},

	count: function(){
		return Array.from(this).length;
	},

	toString: function(){
		return '[object Filter Iterator]';
	}
});

module.exports = FilteredIterable;