module.exports = function(iterable, fn, bind){
	var iterator, next;

	if( typeof iterable['@@iterator'] !== 'function'){
		throw new TypeError('iterable[\'@@iterator\'] is not a function');
	}
	
	iterator = iterable['@@iterator']();
	next = iterator.next();
	while( next.done === false ){
		if( !fn || fn.call(bind, next.value) === true ) break;
		next = iterator.next();
	}

	return this;
};