exports['should wait for resolve to call the compute function'] = function(ComputedObserver, assert){
	var arg = null;
	var computed = ComputedObserver.new(function(values){
		arg = values;
	});

	computed.observe('modelName', {}, 'name', true);

	assert.equal(arg, null);
	computed.resolve();
	assert.equal(typeof arg, 'object');
};

exports['call compute function with an object with values named'] = function(ComputedObserver, assert){
	var arg = null;
	var computed = ComputedObserver.new(function(values){
		arg = values;
	});
	var model = {
		name: 'damien',
		age: 10
	};

	computed.observe('modelName', model, 'name');
	computed.observe('modelAge', model, 'age');
	computed.resolve();
	
	assert.equal(arg.modelName, 'damien');
	assert.equal(arg.modelAge, 10);
};