exports['has the property name'] = function(test){
	var property = test.imports.new('name'), model = {name: 'damien'};

	property.setModel(model);

	test.equal(property.has(), true);
	test.done();
};

exports['get the property value'] = function(test){
	var property = test.imports.new('name'), model = {name: 'damien'};

	part.setModel(model);

	test.equal(property.value, model.name);
	test.done();
}

exports['set the property value'] = function(test){
	var property = test.imports.new('name'), model = {name: 'damien'};

	part.setModel(model);
	part.value = 'sandra';

	test.equal(property.value, 'sandra');
	test.done();
};

exports['propagate model change to nextPart'] = function(test){
	var nameProperty = test.imports.new('name'), firstNameProperty = test.imports.new('firstName'), model;

	model = {
		name: {
			firstName: 'damien'
		}
	};

	nameProperty.nextPart = firstNameProperty;
	nameProperty.setModel(model);
	part.setModel(model);

	test.equal(firstNameProperty.value, model.name.firstName);
	test.done();
};