testModule('../Part', function(Part){

	var part = Part.new('name');
	
	it('has the property name', function(){
		part.setModel({
			name: 'damien'
		});

		expect(part.has()).toBe(true);
	});
	
	it('get the model name damien', function(){
		part.setModel({
			name: 'damien'
		});

		expect(part.value).toBe('damien');
	});

	it('set the model name', function(){		
		part.setModel({
			name: 'damien'
		});

		part.set('sandra');
		expect(part.value).toBe('sandra');
	});

	it('propagate model change to nextPart', function(){

		part.nextPart = Part.new('firstName');
		part.setModel({
			name: {
				firstName: 'damien'
			}
		});

		expect(part.nextPart.value).toBe('damien');

	});	

});