exports['has the property name'] = function(test){
	var part = test.imports.new('name'), model = {name: 'damien'};

	part.setModel(model);

	test.equal(part.has(), true);
	test.done();
};

exports['get the property value'] = function(test){
	var part = test.imports.new('name'), model = {name: 'damien'};

	part.setModel(model);

	test.equal(part.value, model.name);
	test.done();
};

exports['set the property value'] = function(test){
	var part = test.imports.new('name'), model = {name: 'damien'};

	part.setModel(model);
	part.value = 'sandra';

	test.equal(part.value, 'sandra');
	test.done();
};

exports['propagate model change to nextPart'] = function(test){
	var namePart = test.imports.new('name'), firstNamePart = test.imports.new('firstName'), model;

	model = {
		name: {
			firstName: 'damien'
		}
	};

	namePart.nextPart = firstNamePart;
	namePart.setModel(model);
	namePart.setModel(model);

	test.equal(firstNamePart.value, model.name.firstName);
	test.done();
};