var ArraySelector = {
	selectors: null,

	condition: function(selector){
		return selector instanceof Array;
	},

	constructor: function(selector){
		if( selector.length === 0 ){
			return this.super.new(null);
		}
		if( selector.length === 1 ){
			return this.super.new(selector[0]);
		}

		this.super.apply(this, arguments);
		this.selectors = this.selector.map(this.super.new, this.super);
	},

	test: function(item){
		var i = 0, j = this.selectors.length;

		for(;i<j;i++){
			if( !this.selectors[i].match(item) ) return false;
		}
		
		return true;
	}
};

module.exports = ArraySelector;