var Selector = require('./selector');
var BooleanSelector = Selector.create({
	constructor: function(){
		Selector.init.apply(this, arguments);
		
		if( this.selector === true ){
			this.test = this.alwaysTrue;
		}
		else{
			this.test = this.alwaysFalse;
		}
	}
});

module.exports = BooleanSelector;