exports['match nothing for empty array'] = function(test, ArraySelector){
	var selector = ArraySelector.new([]);

	test.equal(selector.match(true), false);
	test.equal(selector.match(false), false);
	test.equal(selector.match(null), false);
	test.equal(selector.match(undefined), false);
};

exports['should match all array values'] = function(test, ArraySelector){
	var selector = ArraySelector.new([false, true]);

	test.equal(selector.match(true), false);
	test.equal(selector.match(false), false);
	test.equal(selector.match(null), false);
	test.equal(selector.match(undefined), false);
};