exports['match anything for true'] = function(test, BooleanSelector){
	var selector = BooleanSelector.new(true);

	test.equal(selector.match(true), true);
	test.equal(selector.match(false), true);
	test.equal(selector.match(null), true);
	test.equal(selector.match(undefined), true);
};

exports['match nothing for false'] = function(test, BooleanSelector){
	var selector = BooleanSelector.new(false);

	test.equal(selector.match(true), false);
	test.equal(selector.match(false), false);
	test.equal(selector.match(null), false);
	test.equal(selector.match(undefined), false);
};