// https://github.com/Polymer/TemplateBinding/blob/master/src/TemplateBinding.js
var Compiler = {
	result: {
		strings: null,
		expressions: null
	},
	//regex: /{([A-Za-z][A-Za-z0-9]*)[^}]*}/g,
	regex: /{([A-Za-z]?[A-Za-z0-9]*)}/g,
	open: '{',
	close: '}',

	createResult: function(strings, expressions){
		this.result.strings = strings;
		this.result.expressions = expressions;
		return this.result;
	},

	compile: function(string){
		var index = 0, regex = this.regex, matches, strings = [], expressions = [];		

		regex.lastIndex = 0;
		while( matches = regex.exec(string) ){
			strings.push(string.slice(index, matches.index));
			expressions.push(matches[1]);
			index = regex.lastIndex;
		}
		strings.push(string.slice(index));

		return this.createResult(strings, expressions);
	}
};

Compiler.compile('Hello {name}{ok} !');

module.exports = Compiler;