exports['compile() returns strings & expression array'] = function(test){
	var result = test.imports.compile('');

	test.ok(result.strings instanceof Array);
	test.ok(result.expressions instanceof Array);
	test.done();
};

exports['Empty string around exppressions'] = function(test){
	var result = test.imports.compile('{name}');
	var strings = result.strings;

	test.equal(strings.length, 2);
	test.equal(strings[0], '');
	test.equal(strings[1], '');
	test.done();
};