var StylizedTemplate = require('../StylizedTemplate');
var proto = require('proto');

var LogTemplate = StylizedTemplate.create({
	ansi: require('ansi'),
	styleRules: Object.create(ansi.styleRules),
	//expressionStyles: Object.create(StylizedTemplate.expressionStyles),
	// own expressionStyles object
	expressionStyles: {},

	_writeStyle: function(string, rules){
		return this.ansi.stylizeAll(string, rules);
	}
});

module.exports = LogTemplate;