exports['render on same template gives != result for != object'] = function(test){
	var tpl = test.imports.new('Hello {name} !');

	test.equal(tpl.render({name: 'damien'}), 'Hello damien !');
	test.equal(tpl.render({name: 'sandra'}), 'Hello sandra !');
	test.done();
};

exports['toArray().join equivalent to toString()'] = function(test){
	var tpl = test.imports.new('test');

	test.equal(tpl.toArray.join(''), tpl.toString());
	test.done();
};