var pair = require('Object.pair');

Number.implement = pair.implement.bind(pair, Number);
Number.complement = pair.complement.bind(pair, Number);

Number.implement({
	limit: function(min, max){
		return Math.min(max, Math.max(min, this));
	},

	toInt: function(base){
		return parseInt(this, base || 10);
	}
});

