module.exports = function(index, length){
	var removed = this.applyMethod('splice', arguments), inserted = Array.slice(arguments, 2), i, j;

	// from spec, negative index are computed as follow
	if( index < 0 ){
		index+= this.array.length;
	}

	i = 0;
	j = removed.length;
	for(;i<j;i++){
		this.notify({
			type: 'remove',
			index: index + i,
			value: removed[i]
		});
	}
	i = 0;
	j = inserted.length;
	for(;i<j;i++){
		this.notify({
			type: 'add',
			index: index + i,
			value: inserted[i]
		});
	}

	return removed;
};