var Selector = require('./selector');

Selector.addConstructor(function(item){ return item instanceof Array; }, require('./arraySelector'));
Selector.addConstructor('boolean', require('./booleanSelector'));
Selector.addConstructor('function', require('./functionSelector'));
Selector.addConstructor(function(selector){ return selector == null; }, require('./nullSelector'));
Selector.addConstructor('number', require('./numberSelector'));
Selector.addConstructor('object', require('./objectSelector'));
Selector.addConstructor(function(item){ return item instanceof RegExp; }, require('./regExpSelector'));
Selector.addConstructor('string', require('./stringSelector'));

module.exports = Selector;