var ResponseHeader = require('./requestHeader');
var AcceptableResponseHeader = ResponseHeader.extend({
	acceptErrorStatusCode: 406, // not acceptable
	acceptErrorMessage: 'Not acceptable response header {headerName}:{headerValue}. Request accepts: {accepteds}',

	getAccepteds: function(){
		return [];
	},

	createAcceptError: function(){
		return this.createError(this.acceptErrorStatusCode, this.acceptErrorMessage, {
			accepteds: this.getAccepteds()
		});
	}	
});

module.exports = AcceptableResponseHeader;