var Router = require('Router');

Object.define(Router.HEADERS, {
	CONTENT_TYPE: 'content-type',
	CONTENT_LENGTH: 'content-length',
	CONTENT_ENCODING: 'content-encoding',
	CONTENT_LANGUAGE: 'content-language',
	CONTENT_MD5: 'content-md5'
});

var proto = require('proto');

module.exports = {
	request: require('./request-content'),
	response: require('./response-content'),

	use: function(options){
		if( options ){
			if( options.request ) proto.define(Router.Request, options.request);
			if( options.response ) proto.define(Router.Response, options.response);
		}
	},

	/*
	handle: function(router){

	}
	*/
};