module.exports = {
	response: {
		methodsAllowed: ['GET', 'POST', 'PUT', 'DELETE', 'OPTIONS'],
		headersAllowed: ['X-Requested-With', 'content-type', 'accept'],

		sendCORS: function(){
			this.populate({
				statusCode: 204,
				headers: {
					'access-control-allow-origin': this.request.getHeader('origin') || '*',
					'access-control-allow-methods': this.methodsAllowed.join(', '),
					'access-control-allow-headers': this.headersAllowed.join(', '),
					'access-control-max-age': 10, // Seconds
					'content-length': 0
				}
			});
		}
	},

	handle: function(request, response, handler){
		if( request.is('options') ){
			response.sendCORS();
		}
		else{
			handler.next();
		}
	}
};