var Router = require('Router');

Router.HEADERS.HTTP_METHOD_OVERRIDE = 'x-http-method-override';
Router.PARAMS.HTTP_METHOD_OVERRIDE = '_method';

module.exports = {
	request: {
		originalMethod: null,

		getMethod: function(){
			var method = this.params[this.router.PARAMS.HTTP_METHOD_OVERRIDE];

			if( !method ) method = this.getHeader(this.router.HEADERS.HTTP_METHOD_OVERRIDE);

			if( method ){
				method = method.toUpperCase();
			}
			else{
				method = this.method;
			}

			return method;
		}
	},

	handle: function(router){
		router.request.originalMethod = router.request.method;
		router.request.method = router.request.getMethod();
		router.next();
	}
};