require('object/assign');

module.exports = {
	name: 'requestParams',

	request: {
		params: null
	},

	handle: function(handler){
		var request = handler.request;
		var params = {};

		Object.assign(params, request.urlParams);
		if( request.body && typeof request.body === 'object' ) Object.assign(params, request.body);

		request.params = params;
	}
};