module.exports = {
	router: null,
	request: null,
	response: null,

	new: function(router){
		this.router = router;
		this.request = router.request;
		this.response = router.response;
	},

	error: function(error){
		this.router.next(error);
	},

	respond: function(data){
		this.emit('response', data);
	},

	handle: function(router){
		return router.next();
	}
};