/*

description: part of a file content

*/

var Splitter = require('Splitter');
var ParentPart = Splitter.Part;

var Part = ParentPart.extend({
	encoding: 'utf8',		// default encoding of buffer
	source: new Buffer(0),	// default source is now a buffer

	// source is converted to buffer
	prepareSource: function(source){
		var buffer = null;

		if( Buffer.isBuffer(source) ){
			buffer = source;
			source = buffer.toString(this.encoding);
		}

		source = ParentPart.prepareSource.call(this, source);

		return buffer || new Buffer(source, this.encoding);
	}
});

module.exports = Part;
