/*

FilePart in JSON format

item: the part as a JavaScript Object

date reviver: http://erraticdev.blogspot.fr/2010/12/converting-dates-in-json-strings-using.html

*/

var Part = require('../FileTemplate/Part');
var JSONPart = Part.extend({
	parse: function(string){
		return JSON.parse(string);
	},

	stringify: function(object){
		return JSON.stringify(object);
	},

	get item(){
		return this.object;
	}
});

module.exports = JSONPart;