var proto = require('@dmail/proto');
var CursorIterator = require('./CursorIterator');

var Cursor = proto.extend({
	headers: null,
	records: null,
	length: null,

	constructor: function(source){
		this.headers = source.shift();
		this.records = source;
		this.length = source.length;
	},

	toEntry: function(record){
		var headers = this.headers, i = 0, j = headers.length, name, entry = {};

		for(;i<j;i++){
			name = headers[i];
			entry[name] = record[i];
		}

		return entry;
	},

	values: function(){
		return new CursorIterator(this, 'value');
	},

	entries: function(){
		return new CursorIterator(this, 'entry');
	}
});

Cursor[Symbol.iterator] = function(){
	return this.entries();
};

module.exports = Cursor;