/*

It's just a DBFileTemplate with schemas

*/

var DBFileTemplate = require('./DBFileTemplate');
var Path = require('path');
var proto = require('proto');

var DBTable = proto.extend.call(DBFileTemplate, {
	name: null,

	setPath: function(path){
		DBTable.super.setPath.call(this, path);
		this.name = Path.basename(this.path, Path.extname(this.path));
	}
});

module.exports = DBTable;