module.exports = {
	'text/plain': function(){
		this.stringifyObject = function(object){
			// for text/plain object are stringified with tabs to be human readable
			return JSON.stringify(object, null, '\t');
		};
	},

	'application/javascript': function(){
		this.format = function(buffer){
			// if a callbackName is provided the result will be wrapped in the callback (JSON-P)
			var callbackName = this.request.params.callback || this.request.params.jsonp;
			if( callbackName ){
				buffer = Buffer.concat([new Buffer(callbackName + '('), buffer, new Buffer(')')]);
			}
			return buffer;
		};
	}
};