module.exports = {
	request: {
		originalMethod: null,
		paramMethodOverride: '_method',

		getMethod: function(){
			var method = this.params[this.paramMethodOverride] || this.getHeader(this.HEADERS.HTTP_METHOD_OVERRIDE);

			if( method ){
				method = method.toUpperCase();
			}
			else{
				method = this.method;
			}

			return method;
		}
	},

	handle: function(){
		this.request.originalMethod = this.request.method;
		this.request.method = this.request.getMethod();
		this.next();
	}
};