require('@dmail/object/toSource');
var proto = require('@dmail/proto');
var MustachePercentTemplate = require('../MustachePercentTemplate');
var StringTemplate = require('template-string');

var ScriptTemplate = proto.extend.call(MustachePercentTemplate, {
	Renderer: proto.extend.call(StringTemplate.Renderer, {
		stringify: Object.toSource
	})
});

module.exports = ScriptTemplate;