require('object/assign');

module.exports = {
	name: 'requestParams',

	handle: function(handler){
		var request = handler.request;
		var params = {};

		Object.assign(params, request.urlParams);
		if( request.bodyParams && typeof request.bodyParams === 'object' ) Object.assign(params, request.bodyParams);

		request.params = params;
	}
};