exports['testing response transformstream'] = function(test){
	var stream = require('stream');
	var pass = new stream.PassThrough();
	var writable = new stream.Writable();

	// uncomment this line make the test fail
	//pass.pipe(writable); pass.unpipe(writable);

	test.willBe(function(resolve, reject){
		pass.on('data', function(chunk){
			resolve(chunk);
		});
		pass.on('error', reject);
	}, 'ok');

	pass.write('ok');
};