exports['plugins are not shared'] = function(test, Router){
	var router = Router.create();
	var requestHandler = router.createRequestHandler();

	test.equal(requestHandler.Request.prototype.plugins.length, 0);
	test.equal(requestHandler.request.plugins.length != 0, true);
};

