require('Object.implement');

var methods = {
	sum: function(){
		var i = this.length, sum = 0, value;
		while(i--){
			value = this[i];
			if( isFinite(value) ){
				sum+= parseFloat(value);
			}
		}
		return sum;
	},

	average: function(){
		return this.length ? this.sum() / this.length : 0;
	},

	max: function(){
		return Math.max.apply(Math, this);
	},

	min: function(){
		return Math.min.apply(Math, this);
	}
};

module.exports = methods;
Object.implement(Array.prototype, methods);