var StylizedTemplate = require('StringTemplate').prototype.StylizedTemplate;
var proto = require('proto');
var ansi = require('ansi');

var ConsoleTemplate = proto.extend(StylizedTemplate, {
	styles: {},
	formats: Object.create(ansi.formats),
	ansi: ansi,	
	//suffix: '\n',

	writeStyle: function(string, format){
		return this.ansi.writeFormat(string, format);
	}
});

module.exports = ConsoleTemplate;